import Answer from "./Answer";
import { useContext } from "react";
import { QuizContext } from "../context/quiz";

const Question = ({questions}) => {
    const [quizState] = useContext(QuizContext);
    console.log("Question", quizState);
    return (
      <div>
        <div className="question">{quizState.Question && quizState.Question.length > 0 && quizState.Question[quizState.currentQuestionIndex] 
    ? quizState.Question[quizState.currentQuestionIndex].Question
    : 'Loading...'}</div> 
        <div className="answers">
            <Answer />
            <Answer />
            <Answer />
            <Answer />
        </div>
      </div>
      
    )
  }
  
  export default Question;
