export const shuffleAnswers = question => {
    const unshuffledAnswers = [
        question.correctAnswer,
        ...question.incorrectAnswers
    ]

    return unshuffledAnswers.map((unshuffledAnswers) => ({
        sort: Math.random(),
        value: unshuffledAnswers,
})).sort((a, b) => a.sort - b.sort).map((a) => a.value);
};

export const normalizeQuestions = backendQuestions => {
    return backendQuestions.map((backendQuestions) => {
        const incorrectAnswers = backendQuestions.incorrect_answers.map((incorrectAnswer) => decodeURIComponent(incorrectAnswer));
        return {
            correctAnswer: decodeURIComponent(backendQuestions.correct_answer),
            question: decodeURIComponent(backendQuestions.question),
            incorrectAnswers: backendQuestions.incorrect_answers.map((incorrectAnswer) => decodeURIComponent(incorrectAnswer)),
        }
    });
};
